document.getElementById("sort-filter-btn").addEventListener("click",()=>{document.getElementById("sort-filter-btn").classList.toggle("focus"),document.getElementById("sort-filter-btn").style.pointerEvents="none",document.getElementById("sort-filter-menu").classList.toggle("hide"),document.getElementById("sort-filter-menu").focus()}),document.getElementById("sort-filter-menu").addEventListener("focusout",()=>{document.getElementById("sort-filter-btn").classList.toggle("focus"),document.getElementById("sort-filter-menu").classList.toggle("hide"),document.getElementById("sort-filter-btn").style.pointerEvents="auto"});